import React, { Component, Fragment } from "react";
import Navbar from "../components/Navbar/Navbar";
import { connect } from "react-redux";
import { withRouter } from 'react-router-dom'
import * as actionTypes from '../store/actions'


class LoginPage extends Component {

  handleEmail = (emailVal) => {
    const { onEmailSave } = this.props
    onEmailSave(emailVal)
  }

  handleEmail = (emailVal) => {
    const { onPasSave } = this.props
    onPasSave(emailVal)
  }

  render() {
    const { email, pas } = this.props
    return (
      <Fragment>
        {/* <Navbar /> */}
        <form action="">
          <div>
            <label htmlFor="email">Email</label>
            <input 
            type="email" 
            id="email" 
            autoComplete="off" 
            value={email} 
            onChange={(e) => this.handleEmail(e.target.value)} 
            required
            />
          </div>
          <div>
            <label htmlFor="password">Password</label>
            <input 
            type="passwrod" 
            id="password" 
            autoComplete="off" 
            value={pas} onChange={(e) => this.handlePas(e.target.value)} 

            />
          </div>

          <button type="submit">Login</button>
        </form>
      </Fragment>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('state', state)
  return {
    email: state.emailReducer.userEmail,
    pas: state.passReducer.userPas
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onPasSave: (pas) => {
      dispatch({
        type: actionTypes.Password,
        payload: pas
      })
    },
    onEmailSave: (email) => {
      dispatch({
        type: actionTypes.Email,
        payload: email
      })
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(withRouter(LoginPage));
