import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { withRouter } from 'react-router-dom'
import * as actionTypes from '../store/actions'
import Navbar from './Navbar/Navbar'

class UserPage extends Component {
  async componentDidMount() {
    const { onEmailSave, onPasSave } = this.props
    const lsEmail = localStorage.getItem('email')
    const lsPas = localStorage.getItem('pas')
    if (lsEmail !== null && lsPas !== null) {
      onEmailSave(lsEmail)
      onPasSave(lsPas)
    } else {
      this.props.history.replace('/login')
    }
  }
  dispEncodePas = (pas) => {
    const strArr = Array.from(pas)
    const endcodedPas = strArr.map(item => item.replace(item, '*')).toString().replace(/\,/g, "")
    console.log('endcodedPas', endcodedPas)
    return endcodedPas
  }
  render() {
    const { email, pas } = this.props
    return (
      <Fragment>
        <Navbar />
        <p>Username: {email}</p>
        <p>Password: {this.dispEncodePas(pas)}</p>
        <br />
        <button onClick={() => this.handleChngPas()}>Change Password</button>
        <button onClick={() => this.handleChng()}>Change Password</button>
      </Fragment>
    );
  }
}


const mapStateToProps = (state) => {
  return {
    email: state.emailReducer.userEmail,
    pas: state.passReducer.userPas
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    onPasSave: (pas) => {
      dispatch({
        type: actionTypes.Password,
        payload: pas
      })
    },
    onEmailSave: (email) => {
      dispatch({
        type: actionTypes.Email,
        payload: email
      })
    }
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(UserPage));
