import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { withRouter } from 'react-router-dom'

class UserPage extends Component {
  dispEncodePas = (pas) => {
    const strArr = Array.from(pas)
    console.log('strArr', strArr)
    const len = strArr.length
    const endcodedPas = strArr.map(item => item.replace(item, '*')).toString()
    console.log('endcodedPas', endcodedPas)
    return endcodedPas
  }
  render() {
    const { email, pas } = this.props
    return (
      <Fragment>
        <p>Userpage</p>
        <p>Username: {email}</p>
        <p>Password: {this.dispEncodePas(pas)}</p>
      </Fragment>
    );
  }
}


const mapStateToProps = (state) => {
  return {
    email: state.emailReducer.userEmail,
    pas: state.passReducer.userPas
  }
}

export default connect(mapStateToProps)(withRouter(UserPage));
